INSERT INTO `news` (`idnew_table`,`Fuente`,`Fecha`,`Titulo`,`TituloMD5`,`Link`,`Descripcion`,`Categoria`,`Texto`,`TextoLematizado`,`WordCountTexto`,`RaizSumatoriaCuadrados`,`Entities`,`EntitiesRecognized`,`numeroDePalabras`,`Images`,`Location`) VALUES (3675,'http://www.lacronica.com','2016-12-20','Hallan corriente a chorro de hierro fundido en núcleo de Tierra',700ddb4c28304450b11b51cd9ec8b3ea,'http://www.lacronica.com/EdicionEnlinea/Notas/CienciayTecnologia/20122016/1161182-Hallan-corriente-a-chorro-de-hierro-fundido-en-nucleo-de-Tierra.html',' Científicos descubrieron una corriente en chorro de hierro fundido dentro del núcleo de la Tierra gracias a los datos de los satélites Swarm de la Agencia Espacial Europea. La corriente se ubica bajo 3 mil kilómetros de roca y se mueve a más de 40 kilómetros por año. Los expertos explican que la corriente es como una banda de aceleración de hierro fundido que circunda el Polo Norte, al igual que la corriente en chorro en la atmósfera','cienciaYtecnologia','Científicos descubrieron una corriente en chorro de hierro fundido dentro del núcleo de la Tierra gracias a los datos de los satélites Swarm de la Agencia Espacial Europea. La corriente se ubica bajo 3 mil kilómetros de roca y se mueve a más de 40 kilómetros por año. Los expertos explican que la corriente es como una banda de aceleración de hierro fundido que circunda el Polo Norte, al igual que la corriente en chorro en la atmósfera.','científico descubrir corriente chorro herrar fundido núcleo tierra gracia dato satélite swarm agencia espacial europeo corriente ubicar 3 mil kilómetro roca mover 40 kilómetro año experto explicar corriente banda aceleración herrar fundido circundar polo norte igual corriente chorro atmósfera','aceleración 1 experto 1 agencia 1 descubrir 1 mover 1 gracia 1 atmósfera 1 científico 1 ubicar 1 40 1 igual 1 swarm 1 3 1 europeo 1 polo 1 norte 1 núcleo 1 espacial 1 dato 1 tierra 1 herrar 2 año 1 chorro 2 fundido 2 banda 1 corriente 4 mil 1 circund