INSERT INTO `news` (`idnew_table`,`Fuente`,`Fecha`,`Titulo`,`TituloMD5`,`Link`,`Descripcion`,`Categoria`,`Texto`,`TextoLematizado`,`WordCountTexto`,`RaizSumatoriaCuadrados`,`Entities`,`EntitiesRecognized`,`numeroDePalabras`,`Images`,`Location`) VALUES (3674,'http://www.lacronica.com','2016-12-20','Falla en app de Uber permitió un fuerte cobro a usuaria',96378539b73cbeac056c95f7a025fef3,'http://www.lacronica.com/EdicionEnlinea/Notas/CienciayTecnologia/20122016/1161191-Falla-en-app-de-Uber-permitio-un-fuerte-cobro-a-usuaria.html',' Uber informó que una falla informática es la responsable de varios cargos excesivos a las tarjetas bancarias de sus usuarios, entre ellos una mujer de Filadelfia  cuyo banco bloqueó un intento de cobrarle más de 28.000 dólares por un traslado. El sitio Philly.com dijo que la mujer recibió un correo electrónico de Uber advirtiéndole que su información financiera había sido hackeada. Seis días más tarde,  Uber  envió otro correo electrónico diciéndole que estaba equivocado, que en realidad se trataba de una falla de parte del gigante de los servicios de taxis solicitados por app y que sus ingenieros estaban por arreglarla.La empresa con sede en San Francisco informó que sus ingenieros están trabajando para asegurar que no vuelva a ocurrir una falla similar. Uber  precisó que el banco de la mujer nunca le cobró 28.639 dólares a la mujer, pero que sí ordenó una retención por esa cantidad en su cuenta','cienciaYtecnologia','Uber informó que una falla informática es la responsable de varios cargos excesivos a las tarjetas bancarias de sus usuarios, entre ellos una mujer de Filadelfia cuyo banco bloqueó un intento de cobrarle más de 28.000 dólares por un traslado.El sitio Philly.com dijo que la mujer recibió un correo electrónico de Uber advirtiéndole que su información financiera había sido hackeada. Seis días más tarde, Uber envió otro correo electrónico diciéndole que estaba equivocado, que en realidad se trataba de una falla de parte del gigan